func assertErr(expected: Double, actual: Double, maxErr: Double, description: String, i: Int) {
    let err = abs((actual - expected) / expected);
    if err > maxErr {
        print("\(description): \(err) > \(maxErr) for i=\(i). expected: \(expected), actual: \(actual)")
    }
